mapper.select:
select e.*,d.number as depNumber,d.name as depName
from table_employee as e
left join table_department as d
on e.dep_id=d.id
where 1=1

    and e.name like concat('%',#{name},'%')



    and e.gender=#{gender}



    and e.age=#{age}



    and e.number=#{number}



    and e.dep_id=#{dep.id}
